// import react and hook to using state in functional component
import React, { useState } from "react";

// import link to use it for chat path
import { link } from "react-router-dom";

// create Join component
const Join = () => {
  // declare hooks and initialize them
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");

  // front end for the join route, what this route render
  return (
    <div className="joinOuterContainer">
      <div className="joinInnerContainer">
        <h1 className="heading">Join</h1>
        <div>
          {" "}
          <input
            placeholder="Name"
            className="joinInput"
            type="text"
            onChange={(event) => setName(event.target.value)}
          />
        </div>
        <div>
          {" "}
          <input
            placeholder="NaRoomme"
            className="joinInput mt-20"
            type="text"
            onChange={(event) => setRoom(event.target.value)}
          />
        </div>

        <link>
          <button className="button mt-20" type="submit">
            {" "}
            Sign In
          </button>
        </link>
      </div>
    </div>
  );
};
export default Join;
